<html><head>	<meta charset="utf-8">	<title>ブログ記事の投稿</title>	<link rel="stylesheet" type="text/css" href="stylesheet.css"></head><body>	<h1 id="midasi">ブログ記事の投稿</h1>	<div id="menu">		<ul>			<li><a href="index.php">トップページ</a></li>			<li><a href="post.php">投稿</a></li>			<li><a href="search.php">検索</a></li>			<br>			<br>		</ul> 	</div>		<?php	if (isset($_POST["title"]) && isset($_POST["contents"])) {		if(!isset($_POST["password"])||$_POST["password"] !== "abc"){			echo "<p>パスワードが違います<p>";		}else{			$title = $_POST["title"];			$contents = $_POST["contents"];			try{				 //タイムゾーンの指定				ini_set("date.timezone", "Asia/Tokyo");				 //$timeへ成形した年月日および時刻データを格納				$time = date("Y.m.d-H:i");				 //PDOクラスのオブジェクトの作成				$dbh = new PDO("sqlite:blog.db"," "," ");				 //実行するSQL文を$sqlに格納				$sql = 'insert into posts(title,contents,date) values(?, ?, ?)';				 //prepareメソッドでSQL文の準備				$sth = $dbh->prepare($sql);				 //prepareした$sthを実行　SQL文の？部に格納する変数を指定				$sth->execute(array($title, $contents, $time));				if ($sth) {					echo "記事１件を投稿しました";				} else {					echo "記事１件の投稿に失敗しました";				}	 			} Catch (PDOException $e) {				print "エラー!: " . $e->getMessage() . "<br/>";				die();			}		}	}	$dbh = null;	?>	<form action="<?php echo $_SERVER['PHP_SELF'] ?>" method="post">		<dl>			<dt><h2>表題：</h2></dt>			<dd><input type="text" name="title" size="60" placeholder="タイトルを入れてください"/></dd>			<dt><h2>本文：</h2></dt>			<dd><textarea name="contents" rows="10" cols="60" placeholder="本文を入れてください"></textarea></dd>			<dt>パスワード:</dt>			<dd><input type="password" name="password" size="20" placeholder="abc"></dd>		</dl>		<input type="reset" value="リセット" class="btn" />		<input type="submit" value="送信" class="btn" />	</form></body></html>